import React from 'react';
import { StyleSheet, Text, View, Image, TextInput, KeyboardAvoidingView, TouchableOpacity } from 'react-native';

export default class Loginscreen extends React.Component {
constructor(){
 super()
 this.state={
  emailId:'',
  password:''
 }
}
render(){
 return(
  <KeyboardAvoidingView style={{alignItems:'center', marginTop:20}}>

   <View>

    <Image source={require("../assets/booklogo.jpg")} 
    style={{ width: 200, height: 200 }} />

    <Text style={{ textAlign: "center", fontSize: 30, fontWeight:"bold", textDecorationLine:'underline' }}>Wily</Text>

  </View>

  <View>

   <TextInput style={{width: 200, height: 40, borderWidth: 2,borderRightWidth: 0, borderRadius:2}}
   placeholder='abc@example.com'
   keyboardType='email.addres'
   onChangeText={(text)=>{
    this.setState({
      emailId:text
    })
   }}/>

   <TextInput style={{width: 200, height: 40, borderWidth: 2,borderRightWidth: 0, borderRadius:2}}
   secureTextEntry={true}
   placeholder='Enter Password'
   onChangeText={(text)=>{
    this.setState({
      password:text
    })
   }}/>

  </View>

  <View>

   <TouchableOpacity style={{heigth:30, width:90, backgroundColor:'red', padding:10, borderRadius:2, borderWidth:2.5}}
   onPress={()=>{
     this.login(this.state.emailId, this.state.password)
   }}>

     <Text style={{textAlign:'center', fontSize:20, fontWeight:'bold', textDecorationLine:'underline'}}> Login </Text>

   </TouchableOpacity>

  </View>

 </KeyboardAvoidingView>
  )
 }
}
